package dandy.com.day1.sub;

public class LoopClass2 {

	public LoopClass2() {
		// TODO Auto-generated constructor stub
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int juara = 1;
     switch (juara) {
	case 1:
		System.out.println(" Juara 1 Dapat Hadiah 20 Juta");
		break;
	case 2:
		System.out.println(" Juara 2 Dapat Hadiah 10 Juta ");
		break;
	case 3:
		System.out.println("Juara 3 Dapat Hadiah 5 Juta ");
		break;

	default:
		System.out.println(" Selain Juara 1,2,3 Dapat Hadiah 1 Juta");
		break;
	}
	}

}
